try {
    home_page_1.default.clickSnatchBotIcon(driver);
    conversation_window_1.default.getChatBotIframe(driver);
    var messageElements = [];
    const waitForAllMessagesToBeLoaded = async () => {
        messageElements = await driver_utils_1.default.findElementsByClassName(driver, conversation_window_1.default.welcomeMesgsElement);
        return messageElements.length === 3;
    };
    // Wait up to 10 seconds for all 3 default message to be loaded
    await driver.wait(waitForAllMessagesToBeLoaded, 10000);
    const allMessages = [];
    for (let i = 0; i < messageElements.length; i++) {
        let text = await messageElements[i].getText();
        allMessages.push(text);
    }
    mocha_utils_1.default.verifyEquals(allMessages[0], chat_window_test_data_1.default.welcomeMesg1, "Welcome message 1 is not showing up correctly");
    mocha_utils_1.default.verifyEquals(allMessages[1], chat_window_test_data_1.default.welcomeMesg2, "Welcome message 2 is not showing up correctly");
    mocha_utils_1.default.verifyEquals(allMessages[2], chat_window_test_data_1.default.welcomeMesg3, "Welcome message 3 is not showing up correctly");
    mocha_utils_1.default.verifyEquals(messageElements.length, 3, "All the three messages are not showing up");
}
catch (error) {
    console.error(error);
}

